CREATE TABLE Branch (B# CHAR(3) PRIMARY KEY, Address VARCHAR(10) UNIQUE NOT NULL);
CREATE TABLE Customer (C# CHAR(5) PRIMARY KEY, Name VARCHAR(10) UNIQUE NOT NULL);
CREATE TABLE Account (B# CHAR(3), A# CHAR(4), C# CHAR(5), Balance FLOAT DEFAULT 0 CHECK(Balance>=0), PRIMARY KEY(B#, A#), FOREIGN KEY (B#) REFERENCES Branch(B#) ON DELETE CASCADE, FOREIGN KEY (C#) REFERENCES Customer(C#) ON DELETE CASCADE);